import React from 'react';
import api from '../services/OpenAI';

class PetNamesForm extends React.Component{
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            value: '',
            specie: '',
            petNames: [],
            api: api,
        };
    }

    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(event){
        this.consultPetNames();
        event.preventDefault();
    }

    render(){
        return (
            <><div className="pet-container-form">
                <form name="pet_name_form" id="pet_name_form" onSubmit={this.handleSubmit}>
                    <label>
                        Specie of your pet:
                    </label>
                    <select value={this.state.value} 
                            onChange={this.handleChange}
                            name="pet_specie" 
                            id="pet_specie" 
                            required>
                        <option value="">-- Select an option --</option>
                        <option value="dog">Dog</option>
                        <option value="cat">Cat</option>
                        <option value="bird">Bird</option>
                        <option value="fish">Fish</option>
                        <option value="snake">Snake</option>
                    </select>
                    <label className='submit-buttom'>
                        <input type="submit" value="Submit" />
                    </label>
                </form>
                {this.consultPetNames()}
            </div>
            </>
        )
    }

    consultPetNames(){
        if (this.state.value === ''){
            return;
        }
        console.log(this.state.value);
        this.state.api.post("nlp-pet-name/specie/"+this.state.value).then(
            res =>{
                this.setState({ specie: res.data.specie });
                this.setState({ petNames: res.data.names});
            });
        return(
            <div className="result-pet-names">
            <ul>
                {this.state.petNames}
            </ul>
        </div>
        )
    }

}

export default PetNamesForm;
