import api from "./OpenAI";

function getPetNames(specieIn) {
  const petNames = [];
  const specie = specieIn;
  makeOpenAIRequest(specie).then((response) => {
    const petNames = response.names;
  });

  return(
    <div className="result-pet-names">
      <ul>
        {console.log(petNames)}
        {console.log(specie)}
        {/* {this.state.petNames.map(petName => <li>{petName.names}</li>)} */}
      </ul>
    </div>
  )

}

