import React from 'react';

class ImageGeneratorForm extends React.Component{
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            imageDescription: '',
            inputImageDescription: '',
            changeValue: false,
        };
    }

    handleChange(event){
        this.setState({changeValeu: false});
        this.setState({inputImageDescription: event.target.value});
    }

    handleClick(event){
        event.preventDefault();
        this.setState({imageDescription: this.state.inputImageDescription});
        this.setState({changeValeu: true});
    }

    render(){
        return (
            <>
            <div className="image-container-form">
            <form name="image-form" id="image-form">
                <label>
                Image the descripitons:
                </label>
                <input  type="text" 
                        name="image-description" 
                        id="image-description"
                        value={this.state.value} 
                        onChange={this.handleChange}
                        required />
                <label className='submit-buttom'>
                <input type="submit" value="Submit" onClick={this.handleClick} />
                </label>
            </form>
            </div>
            <div className="result-image-generated">
                {this.state.changeValue && this.state.imageDescription !== '' ? 
                    this.state.imageDescription
                    : 
                    null
                }
                <img src={this.state.url} alt={this.state.value} />
            </div>
            </>
        )
    }
}

export default ImageGeneratorForm;
