import '../css/PetNames.css';
import React, { useEffect, useState } from 'react';
import api from '../services/OpenAI';

function PetNames() {
  const [petNames, setPetNames] = React.useState([]);

  useEffect(() => {
    api
      .post("/nlp-pet-name/?specie=dog")
      .then((response) => setPetNames(response.data))
      .catch((err) => {
        console.error("ops! ocorreu um erro" + err);
      });
  }, []);

  return (
    <>
    <h2>Choose a specie to name your pet</h2>
    <div className="pet-container-form">
      <form name="pet-name-form" id="pet-name-form" method='POST' action="#">
        <label>
          Specie of your pet: 
        </label>
          <select name="pet-specie" id="pet-specie">
            <option value="dog">Dog</option>
            <option value="cat">Cat</option>
            <option value="bird">Bird</option>
            <option value="fish">Fish</option>
            <option value="snake">Snake</option>
          </select>
        <label className='submit-buttom'>
          <input type="submit" value="Submit" />
        </label>
      </form>
    </div>
    <div className="result-pet-names">
      <p>{petNames?.names}</p>
    </div>
    </>
  );
}

export default PetNames;
