import React from 'react';
import PetNamesComponent from './PetNamesComponent';

class PetNamesForm extends React.Component{
    constructor(props){
        super(props);
        this.handlePetNamesSubmit = this.handlePetNamesSubmit.bind(this);
        this.state = {petSpecie: ''};
    }

    handlePetNamesSubmit(){
        this.setState({petSpecie: this.props.specie});
        this.getPetNames(this.state.petSpecie);
    }

    render(){
        return (
            <><div className="pet-container-form">
                <form name="pet_name_form" id="pet_name_form">
                    <label>
                        Specie of your pet:
                    </label>
                    <select name="pet_specie" id="pet_specie" required>
                        <option value="">Choose an option</option>
                        <option value="dog">Dog</option>
                        <option value="cat">Cat</option>
                        <option value="bird">Bird</option>
                        <option value="fish">Fish</option>
                        <option value="snake">Snake</option>
                    </select>
                    <label className='submit-buttom'>
                        <input type="submit" value="Submit" 
                        onSubmit={this.handleClick()} />
                    </label>
                </form>
            </div>
            </>
        )
    }

    getPetNames(){
        return <PetNamesComponent specie={this.state.petSpecie} />
    }

}

export default PetNamesForm;
