import React from 'react';
import {api} from '../services/OpenAI';
import OpenAINLP from '../services/OpenAINLP';

class PetNamesForm extends React.Component{
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            value: '',
            api: api,
        };
        this.openAINLP = new OpenAINLP();
    }

    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(event){

        this.openAINLP.GetPetNames(this.state.value);
        event.preventDefault();
    }

    render(){
        return (
            <><div className="pet-container-form">
                <form name="pet_name_form" id="pet_name_form" onSubmit={this.handleSubmit}>
                    <label>
                        Specie of your pet:
                    </label>
                    <select value={this.state.value} 
                            onChange={this.handleChange}
                            name="pet_specie" 
                            id="pet_specie" 
                            required>
                        <option value="">-- Select an option --</option>
                        <option value="dog">Dog</option>
                        <option value="cat">Cat</option>
                        <option value="bird">Bird</option>
                        <option value="fish">Fish</option>
                        <option value="snake">Snake</option>
                    </select>
                    <label className='submit-buttom'>
                        <input type="submit" value="Submit" />
                    </label>
                </form>
                <div className="result-pet-names">
                    <ul>
                        {console.log(this.openAINLP.state.openAIresponse)}
                        {console.log(this.state.value)}
                    </ul>
                </div>
            </div>
            </>
        )
    }



}

export default PetNamesForm;
