import React from 'react';
import PetNamesComponent from './PetNamesComponent';

class PetNamesForm extends React.Component{
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            petSpecie: '',
        };
    }

    handleChange(event){
        this.setState({petSpecie: event.target.value});
    }

    handleSubmit(event){
        console.log('submit');
        this.setState({petSpecie: event.state.value});
    }

    render(){
        return (
            <><div className="pet-container-form">
                <form name="pet_name_form" id="pet_name_form" onSubmit={this.handleSubmit}>
                    <label>
                        Specie of your pet:
                    </label>
                    <select value={this.state.value} 
                            onChange={(event) => this.handleChange}
                            name="pet_specie" 
                            id="pet_specie" 
                            required>
                        <option value="dog">Dog</option>
                        <option value="cat">Cat</option>
                        <option value="bird">Bird</option>
                        <option value="fish">Fish</option>
                        <option value="snake">Snake</option>
                    </select>
                    <label className='submit-buttom'>
                        <input type="submit" value="Submit" 
                        onSubmit={() => this.handleSubmit()} />
                    </label>
                </form>
            </div>
            </>
        )
    }

    getPetNames(petSpecie){
        console.log(petSpecie);
        return <PetNamesComponent specie={petSpecie} />
    }

}

export default PetNamesForm;
