import React from 'react';
import api from '../services/OpenAI';

class ImageGeneratorForm extends React.Component{
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            value: '',
            url: '',
            api: api,
        };
    }

    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(event){
        event.preventDefault();
        this.consultImageGenerator();
        console.log(this.state.url);
    }

    render(){
        return (
            <>
            <div className="image-container-form">
            <form name="image-form" id="image-form" onSubmit={this.handleSubmit}>
                <label>
                Image the descripitons:
                </label>
                <input  type="text" 
                        name="image-description" 
                        id="image-description" />
                <label className='submit-buttom'>
                <input type="submit" value="Submit" />
                </label>
            </form>
            </div>
            <div className="result-image-generated">
                {this.state.value}
                <img src={this.state.url} alt={this.state.value} />
            </div>
            </>
        )
    }

    async consultImageGenerator(){
        if (this.state.value === ''){
            return;
        }
        try {
            const res = await fetch(url, {
              method: 'POST',
              body: JSON.stringify(comment),
              headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Accept-Language': 'en-US',
              },
            });
            return res;
          } catch (e) {
            console.log(e);
          }
    }

}

export default ImageGeneratorForm;
