import React from 'react';
import {api} from '../services/OpenAI';
import OpenAINLP from '../services/OpenAINLP';

class PetNamesForm extends React.Component{
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            value: '',
            response: {
                names: [],
                specie: "",
            },
            api: api,
        };
    }
    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(event){
        const openAINLP = new OpenAINLP();
        openAINLP.GetPetNames(this.state.value);
        this.setState({response: openAINLP.state.openAIresponse});
        this.test(event);

    }
    test(event){
        event.preventDefault();
    }

    render(){
        const renderResponse = () => {
            if(this.state.response.names.length > 0){
                const res = (
                <div className="result-pet-names">
                    <ul>
                        <li>Specie: {this.state.response.specie}</li>
                    </ul>
                </div>
                );
                return res;
            }
        }
        return (
            <><div className="pet-container-form">
                <form name="pet_name_form" id="pet_name_form" onSubmit={this.handleSubmit}>
                    <label>
                        Specie of your pet:
                    </label>
                    <select value={this.state.value} 
                            onChange={this.handleChange}
                            name="pet_specie" 
                            id="pet_specie" 
                            required>
                        <option value="">-- Select an option --</option>
                        <option value="dog">Dog</option>
                        <option value="cat">Cat</option>
                        <option value="bird">Bird</option>
                        <option value="fish">Fish</option>
                        <option value="snake">Snake</option>
                    </select>
                    <label className='submit-buttom'>
                        <input type="submit" value="Submit" />
                    </label>
                </form>
            </div>
            {renderResponse()}
            </>
        )
    }



}

export default PetNamesForm;
