import React from 'react';
import {api, baseURL} from '../services/OpenAI';

class ImageGeneratorForm extends React.Component{
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            value: '',
            url: '',
            urlBase: baseURL,
        };
    }

    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(event){
        event.preventDefault();
        const response = this.consultImageGenerator();
        response.then(res => {
            this.setState({url: res.url});
        });
    }

    render(){
        return (
            <>
            <div className="image-container-form">
            <form name="image-form" id="image-form" onSubmit={this.handleSubmit}>
                <label>
                Image the descripitons:
                </label>
                <input  type="text" 
                        name="image-description" 
                        id="image-description"
                        value={this.state.value} 
                        onChange={this.handleChange} />
                <label className='submit-buttom'>
                <input type="submit" value="Submit" />
                </label>
            </form>
            </div>
            <div className="result-image-generated">
                {this.state.value}
                <img src={this.state.url} alt={this.state.value} />
            </div>
            </>
        )
    }

    async consultImageGenerator(){
        if (this.state.value === ''){
            return;
        }
        try {
            const url = this.state.urlBase+"cv-image-generator/description/"+this.state.value;
            const res = await fetch(
                url, {
                method: 'POST',
            });
            return res.data;
          } catch (e) {
            console.log(e);
          }
    }

}

export default ImageGeneratorForm;
