import React from 'react';
import PetNamesResultComponent from './PetNamesResultComponent';

class PetNamesForm extends React.Component{
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            value: '',
        };
    }
    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(event){
        event.preventDefault();
    }

    handleShowResult(event){
        return;
    }

    render(){
        return (
            <><div className="pet-container-form">
                <form name="pet_name_form" id="pet_name_form" onSubmit={this.handleSubmit}>
                    <label>
                        Specie of your pet:
                    </label>
                    <select value={this.state.value} 
                            onChange={this.handleChange}
                            name="pet_specie" 
                            id="pet_specie" 
                            required>
                        <option value="">-- Select an option --</option>
                        <option value="dog">Dog</option>
                        <option value="cat">Cat</option>
                        <option value="bird">Bird</option>
                        <option value="fish">Fish</option>
                        <option value="snake">Snake</option>
                    </select>
                    <label className='submit-buttom'>
                        <input type="submit" value="Submit" />
                    </label>
                </form>
            </div>
            if (this.state.value != "") {
                <p>a</p>
            }

            {this.state.value}
            <PetNamesResultComponent specie={this.state.value}/>
            </>
        )
    }



}

export default PetNamesForm;
