import {React} from 'react';
import {baseURL} from '../services/OpenAI';

class ImageGeneratorForm extends React.Component{
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            value: '',
            url: '',
            urlBase: baseURL,
        };
    }

    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(event){
        event.preventDefault();
    }

    render(){
        return (
            <>
            <div className="image-container-form">
            <form name="image-form" id="image-form" onSubmit={this.handleSubmit}>
                <label>
                Image the descripitons:
                </label>
                <input  type="text" 
                        name="image-description" 
                        id="image-description"
                        value={this.state.value} 
                        onChange={this.handleChange}
                        required />
                <label className='submit-buttom'>
                <input type="submit" value="Submit" />
                </label>
            </form>
            </div>
            <div className="result-image-generated">
                <img src={this.state.url} alt={this.state.value} />
            </div>
            </>
        )
    }
}

export default ImageGeneratorForm;
